using UnityEngine;public class Girl2D : MonoBehaviour{	public enum EAnimDir { EAD_Left = 1, EAD_Right, EAD_Up, EAD_Down };	public EAnimDir m_AnimDir = EAnimDir.EAD_Left;	public Material m_MatOutline;	public Color m_OutlineColor = Color.white;	[Range(1f, 3f)] public float m_OutlineThickness = 1f;	public bool m_OutlineOnly = false;	public bool m_RevertWhenMouseExit = true;		private Animator m_Animator;	private Material m_MatOrig;	private SpriteRenderer m_SprRdr;		void Start ()	{		m_Animator = this.GetComponent<Animator> ();		m_SprRdr = this.GetComponent<SpriteRenderer> ();		m_MatOrig = m_SprRdr.material;	}	void Update ()	{		if (m_AnimDir == EAnimDir.EAD_Left)			m_Animator.SetInteger ("Direction", 1);		else if (m_AnimDir == EAnimDir.EAD_Right)			m_Animator.SetInteger ("Direction", 2);		else if (m_AnimDir == EAnimDir.EAD_Up)			m_Animator.SetInteger ("Direction", 3);		else if (m_AnimDir == EAnimDir.EAD_Down)			m_Animator.SetInteger ("Direction", 4);					m_SprRdr.material.SetColor ("_OutlineColor", m_OutlineColor);		m_SprRdr.material.SetFloat ("_OutlineThickness", m_OutlineThickness);		if (m_OutlineOnly)			m_SprRdr.material.EnableKeyword ("OUTLINE_ONLY");		else			m_SprRdr.material.DisableKeyword ("OUTLINE_ONLY");	}	void OnMouseOver ()	{		m_SprRdr.material = m_MatOutline;	}	void OnMouseExit ()	{		if (m_RevertWhenMouseExit)			m_SprRdr.material = m_MatOrig;	}}